XWiki.UserWorkspaceTab = Class.create({

  initialize : function () {
    this.addListeners();
  },

  addListeners : function() {
    var elements = $('workspaces').childElements();
    for (var key in elements) {
      var element = elements[key];
      var workspaceName = element.id;
      var link = element.down('a');
      link.observe('click', this.onWorkspaceClicked);
    }
  },

  onWorkspaceClicked : function (event) {
    var element = Event.element(event);
    var workspaceId = element.up().id;
    new Ajax.Request("$xwiki.getURL('WorkspaceManager.WorkspaceActivityServiceCode')", {
      parameters: {
        'xpage' : 'plain',
        'workspaceName' : workspaceId
      },

      onCreate : function() {
        $('workspaceActivityColumn').innerHTML = '<span class="loading">Loading...</span>';
      }.bind(this),

      onSuccess : function(response) {
        $('workspaceActivityColumn').innerHTML = response.responseText;
      }.bind(this),

      onFailure: function(response) {
        var failureReason = response.statusText;
        if (response.statusText == '' /* No response */ || response.status == 12031 /* In IE */) {
          failureReason = 'Server not responding';
        }
        $('workspaceActivityColumn').innerHTML = '<span>Failed to load activity stream. Reason:</span><br/><span>' + failureReason + '</span>';
      }.bind(this),

      on0: function (response) {
        response.request.options.onFailure(response);
      }.bind(this),

      onComplete : function() {
      }
    });
  }
});

document.observe('xwiki:dom:loaded', function() {
  new XWiki.UserWorkspaceTab();
});
